items.listeners:
			{
             	'beforequery' : function(e){
                   var combo = e.combo;
                   combo.store.filterBy(function(record){
	                	var id = record.get("id");
	                	if(id=='99'){
	                		return false;
	                	}
		                return id.length==2 || id.length==1;
             	 	});
	             	combo.onLoad();
	             	return false;
           		},
           		select:function(){
           			this.findParentByType("form").manager.query();
           		}
            }
